import re

with open('RNA codon table', 'r') as rna_codons:
    raw_txt = rna_codons.read()
    codon_dict = {}
    codons = re.findall('[AUGC]{3}', raw_txt)
    amino_acids = re.findall('(?<=\s)(\w|Stop)(?=\s)', raw_txt)
    for codon, aa in zip(codons, amino_acids):
        codon_dict[codon] = aa

sample_dataset = ''

primary_seq = []

for i in range(len(sample_dataset)):
    if i % 3 == 0:
        aa = codon_dict[sample_dataset[i: i+3]]
        if aa != 'Stop':
            primary_seq.append(aa)

print(''.join(primary_seq))
